    const [contenido, setContenido] = useState([])

    useEffect(()=>{
        const fetchAlumno = async ()=>{
            try{
                //http://localhost:8800/juegos
                const res = await axios.get("http://localhost:8800/Alumno")
                setContenido(res.data);
            }catch(err){
                console.log(err)
            }
        }
        fetchAlumno()
    },[])

   

    console.log(contenido)   

